          <Route path="/" element={<Daily />} />
          <Route path="/progress" element={<Progress />} />
        </Routes> */}
      </section>
    </main>
  );
}

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
