import "../styles/PanelCard.css";
import Grid from "./Grid";

type ProgressCardProps = {
  currentStreak: number;
  longestStreak: number;
};

function ProgressCard({ currentStreak, longestStreak }: ProgressCardProps) {
  return (
    <main className="panel-card">
      <section className="panel-card-header">
        <h3
          style={{
            color: "transparent",
            backgroundImage:
              "linear-gradient(45deg, var(--orange-color), var(--text-color))",
            WebkitBackgroundClip: "text",
            backgroundClip: "text",
            fontWeight: "bold",
          }}
        >
          {longestStreak} days
        </h3>
        <h3>{currentStreak} days</h3>
      </section>
      <section className="panel-card-divider"></section>
      <section className="panel-card-relation">
        <Grid />
      </section>
    </main>
  );
}

export default StreakCard;
