import React, { useState } from 'react';
import '../styles/Waitlist.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faEnvelope } from '@fortawesome/free-regular-svg-icons';
import TextField from '../components/TextField';

const Waitlist = React.forwardRef((props, ref) => {

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');

    const handleSubmit = async () => {
        const scriptURL = 'https://script.google.com/macros/s/AKfycbw6ynV5-1RubUGv743CD6EzX0fSkTj7C8A-71D028NAAsEWu2id1E7g6PPbSs1lcfdQ/exec';
        const formData = new FormData();
        formData.append('name', name);
        formData.append('email', email);

        try {
            const response = await fetch(scriptURL, {
            method: 'POST',
            body: formData,
            mode: 'no-cors'
            });

            // Since we can't access the response status due to CORS, we'll assume success
            alert('Thank you for joining the waitlist!');
            setName('');
            setEmail('');
        } catch (error) {
            console.error('Error:', error);
            alert('An error occurred. Please try again later.');
        }
    };

    return (
        <main className='Waitlist' ref={ref}>
            <section className='waitlist-content'>
                <section className='waitlist-header'>
                    <h1>
                        Together, letâ€™s get people chatting again.
                    </h1>
                    <h3>
                        Join the waitlist and help bring meaningful
                        <br />
                        conversations back into daily life.
                    </h3>
                </section>
                <section className='waitlist-buttons'>
                    <TextField
                        icon={<FontAwesomeIcon icon={faUser} />}
                        placeholder='Name'
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                    <TextField
                        icon={<FontAwesomeIcon icon={faEnvelope} />}
                        placeholder='Email'
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </section>
                <button onClick={handleSubmit}>
                    Let's do it
                </button>
            </section>
        </main>
    );
});

export default Waitlist;
