import React from 'react';

function TextField({ icon, placeholder }) {
  return (
    <div style={{
      display: 'flex',
      alignItems: 'center',
      border: '2px solid #ccc',   // Border stroke width and color
      borderRadius: '10px',        // Rounded borders
      padding: '8px 12px',         // Inner padding
      width: '100%',               // Full width of parent container
      maxWidth: '300px',           // Optional: Max width for text field
    }}>
      {/* Icon on the left */}
      <span style={{
        marginRight: '8px',        // Space between icon and text
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
      }}>
        {icon}
      </span>

      {/* Input field */}
      <input
        type="text"
        placeholder={placeholder}
        style={{
          border: 'none',          // Removes the inner input border
          outline: 'none',         // Removes outline on focus
          flex: '1',               // Takes up remaining space
          fontSize: '16px',        // Text font size
        }}
      />
    </div>
  );
}

export default TextField;
