import React from 'react';

function TextField({ icon, placeholder }) {
  return (
    <div style={{
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        width: '415px',
        height: '65px',
        flexShrink: '0',
        borderRadius: '100px',
        border: '2px solid var(--white-color, #FBF7F4)',
        background: 'var(--offwhite-color, #F6F0EB)',
        boxShadow: '0px 10px 20px 0px rgba(194, 194, 194, 0.2)',
        fontFamily: 'Poppins, sans-serif',
        fontSize: '25px',
        fontStyle: 'normal',
        fontWeight: '400',
        lineHeight: '40px',
        padding: '0px 30px',
        margin: '0px',
        gap: '10px'
      }}>

      {/* Icon on the left */}
      <span style={{
        color: '#B0B0B0',       // Set icon color to #B0B0B0
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        fontSize: '20px'
      }}>
        {icon}
      </span>

      {/* Input field */}
      <input
        type="text"
        placeholder={placeholder}
        style={{
          border: 'none',           // Removes the inner input border
          outline: 'none',          // Removes outline on focus
          flex: '1',                // Takes up remaining space
          fontSize: '16px',         // Text font size
          background: 'var(--offwhite-color, #F6F0EB)',
          color: '#333',            // Set input text color
          '::placeholder': {        // Set placeholder color
            color: '#B0B0B0',
          }
        }}
      />
    </div>
  );
}

export default TextField;
